    ) VALUES (1480484, 'Glasdegib', 'Primidone', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of glasdegib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of glasdegib with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
