    ) VALUES (1480562, 'Rifampicin', 'Primidone', 'Minor', 'Rifampin may decrease the plasma concentrations of barbiturates by inducing hepatic metabolism. When given concomitantly with rifampin, patients should be monitored for inadequate response to barbiturates or decreases in serum concentrations, with dosages adjusted as necessary.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
