        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1480596, 'Sunitinib', 'Primidone', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may decrease the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', NULL, 'Concomitant use of sunitinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of sunitinib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with minimal or no enzyme induction potential should be considered whenever possible. (Note: This recommendation is based on pharmacokinetic data from healthy volunteers as described above. The safety and efficacy of sunitinib with concomitant CYP450 3A4 inducers have not been established. )', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
