        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1480603, 'Telaprevir', 'Primidone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of the hepatitis C virus (HCV) NS3/4A protease inhibitors, boceprevir and telaprevir, which are metabolized by the isoenzyme.', NULL, 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer.', NULL, 'Caution is recommended if boceprevir or telaprevir is used in combination with a CYP450 3A4 inducer. Close clinical monitoring of the virologic response to the HCV NS3/4A protease inhibitor is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
