INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481154, 'Fedratinib', 'Progesterone', 'Moderate', 'Coadministration of fedratinib may increase the plasma concentrations of drugs that are substrates of CYP450 3A4, 2C19, or 2D6 isoenzymes.', 'DDInter', 1767317477, 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, or 2D6. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fedratinib is added to or withdrawn from therapy, particularly those with a narrow therapeutic index.', 'Metabolism', 'Caution is recommended when fedratinib is used concomitantly with substrates of CYP450 3A4, 2C19, or 2D6.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481155, 'Felbamate', 'Progesterone', 'Moderate', 'RECOMMENDED: Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins. Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary. For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers. Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481156, 'Fidaxomicin', 'Progesterone', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter. Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481157, 'Fluconazole', 'Progesterone', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins. The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317477, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481158, 'Fluvoxamine', 'Progesterone', 'Moderate', 'Coadministration with fluvoxamine may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to competitive inhibition of CYP450 3A4 activity by fluvoxamine.', 'DDInter', 1767317477, 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever fluvoxamine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if fluvoxamine must be used concomitantly with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
