INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481220, 'Guanadrel', 'Promethazine', 'Moderate', 'Phenothiazines may inhibit the antihypertensive effect of guanethidine, probably through inhibition of guanethidine neuronal uptake. No data are available for guanadrel, but a similar interaction is suspected. Other adrenergic neurone blockers may also interact.', 'DDInter', 1767317477, 'This combination should generally be avoided. An alternative antihypertensive regimen may be necessary for patients who must take phenothiazine antipsychotics. If patients do receive a phenothiazine with an adrenergic neurone blocker, their blood pressure should be monitored closely. The dosage of the antihypertensive may require adjustment.', 'Antagonism', 'This combination should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481221, 'Guanethidine', 'Promethazine', 'Moderate', 'Phenothiazines may inhibit the antihypertensive effect of guanethidine, probably through inhibition of guanethidine neuronal uptake. No data are available for guanadrel, but a similar interaction is suspected. Other adrenergic neurone blockers may also interact.', 'DDInter', 1767317477, 'This combination should generally be avoided. An alternative antihypertensive regimen may be necessary for patients who must take phenothiazine antipsychotics. If patients do receive a phenothiazine with an adrenergic neurone blocker, their blood pressure should be monitored closely. The dosage of the antihypertensive may require adjustment.', 'Antagonism', 'This combination should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481222, 'Halothane', 'Promethazine', 'Moderate', 'Theoretically, concurrent use of two or more drugs that can cause QT interval prolongation may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. The risk of an individual agent or a combination of these agents causing ventricular arrhythmia in association with QT prolongation is largely unpredictable but may be increased by certain underlying risk factors such as congenital long QT syndrome, cardiac disease, and electrolyte disturbances.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution and clinical monitoring are recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481223, 'Imipramine', 'Promethazine', 'Moderate', 'Coadministration of a phenothiazine with a tricyclic antidepressant (TCA) may result in elevated plasma concentrations of one or both drugs as well as additive adverse effects. Most phenothiazines and TCAs have been found to undergo metabolism by CYP450 2D6, thus competitive inhibition of the enzyme may occur when more than one of these agents are administered.', 'DDInter', 1767317477, 'Concurrent use of phenothiazines and TCAs should be approached with caution, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication (e.g., abdominal pain, fever, heat intolerance, blurred vision, confusion, hallucinations) or cardiovascular toxicity (e.g., dizziness, palpitations, arrhythmias, syncope). Ambulatory patients should be counseled to avoid activities requiring mental alertness until they know how these agents affect them.', 'Metabolism', 'Concurrent use of phenothiazines and TCAs should be approached with caution, particularly in the elderly and those with underlying organic brain disease, who tend to be more sensitive to the central anticholinergic effects of these drugs and in whom toxicity symptoms may be easily overlooked.', NULL, NULL, 'Moderate', 'pharmacodynamic');
