INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481328, 'Propafenone', 'Oxtriphylline', 'Moderate', 'Propafenone may inhibit the clearance of theophyllines. The exact mechanism is not known. Serum theophylline concentration and the risk of toxicity may be increased. Patients with chronic obstructive pulmonary disease, congestive heart failure, or cirrhosis may have slower theophylline clearance rates; therefore, they may be at greater risk of developing theophylline toxicity. Also, the clinician should be aware of the risk that bronchospasm may be induced or exacerbated by propafenone. These drugs may have antagonistic pharmacologic effects on the patient''s airways.', 'DDInter', 1767317477, 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended. Patients should be advised to notify their physician if they experience a worsening of their respiratory symptoms or any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats. It may be necessary to reduce theophylline dosage.', 'Metabolism', 'If these drugs are given concurrently, close clinical and laboratory monitoring of response and tolerance is recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481329, 'Propafenone', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 1767317477, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481330, 'Propafenone', 'Penbutolol', 'Moderate', 'Propafenone may significantly increase serum levels and effects of some oral beta-blockers. The proposed mechanism is inhibition of CYP450 2D6 first-pass metabolism and decreased hepatic clearance. Data are available for metoprolol and propranolol only; however, other hepatically metabolized oral beta-blockers may also be affected. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact. In addition, negative inotropic effects may be potentiated.', 'DDInter', 1767317477, 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes. Beta-blocker dosage may be decreased if necessary.', 'Metabolism', 'Patients receiving this combination should be monitored for hypotension, heart failure, bradycardia, arrhythmias, and mental status changes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
