INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481361, 'Secobarbital', 'Propafenone', 'Minor', 'Some barbiturates increase the hepatic metabolism of propafenone. Plasma levels and efficacy of propafenone may be decreased. Data are available for phenobarbital. Propafenone efficacy should be monitored during coadministration, and plasma propafenone levels should be checked as necessary. It may be necessary to increase propafenone dosage.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
