INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481363, 'Selpercatinib', 'Propafenone', 'Major', 'Selpercatinib can cause concentration-dependent prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval. An electrocardiogram and serum electrolyte levels should be obtained prior to initiating selpercatinib therapy and periodically during treatment as appropriate based on individual risk factors including diarrhea. Correct hypokalemia, hypomagnesemia, and/or hypocalcemia before starting treatment and during treatment, as they may be risk factors for ventricular arrhythmias.', 'Synergism', 'Caution is recommended if selpercatinib is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481364, 'Sertraline', 'Propafenone', 'Moderate', 'Coadministration with certain selective serotonin reuptake inhibitors (SSRIs) may increase the plasma concentrations of propafenone. The mechanism is SSRI inhibition of CYP450 2D6, the isoenzyme responsible for the metabolic clearance of propafenone in most people.', 'DDInter', 1767317477, 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with propafenone. Lower initial dosages of the antiarrhythmic agent may be appropriate. Patients who are already stabilized on their antiarrhythmic regimen should be monitored for altered effects on myocardial conduction following addition or discontinuation of SSRI therapy (up to 2 weeks for most SSRIs and 5 weeks for fluoxetine due to its long half-life), and the antiarrhythmic dosage adjusted if necessary. Alternatively, an antidepressant that does not interfere with CYP450 2D6 metabolism may be considered, such as citalopram, escitalopram, or venlafaxine.', 'Metabolism', 'Caution is advised if certain SSRIs, particularly fluoxetine or paroxetine, must be used concomitantly with propafenone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481365, 'Sevoflurane', 'Propafenone', 'Moderate', 'Sevoflurane may cause prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended if sevoflurane is used in combination with other drugs that can prolong the QT interval. The patient''s cardiovascular status and serum electrolytes should be closely monitored.', 'Synergism', 'Caution is recommended if sevoflurane is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
