INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481399, 'Trimipramine', 'Propafenone', 'Moderate', 'Coadministration with Class IC antiarrhythmic agents may increase the plasma concentrations of some tricyclic antidepressants (TCAs). The proposed mechanism is inhibition of CYP450 2D6, the isoenzyme primarily or partially responsible for the metabolism of most TCAs. Class IC antiarrhythmic agents can cause prolongation of the QT interval. Theoretically, coadministration with other agents such as TCAs that can prolong the QT interval may increase the risk of ventricular arrhythmias, including ventricular tachycardia and torsade de pointes, because of additive arrhythmogenic potential related to their effects on cardiac conduction.', 'DDInter', 1767317477, 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant. Pharmacologic response and serum TCA levels should be monitored more closely whenever a class IC antiarrhythmic agent is added to or withdrawn from therapy, and the TCA dosage adjusted as necessary. Patients should be advised to notify their physician if they experience possible signs and symptoms of TCA toxicity such as excessive sedation, dry mouth, blurred vision, urinary retention, constipation, tachycardia, arrhythmia, and seizures. Patients should seek medical attention if they experience symptoms that could indicate the occurrence of torsades de pointes such as dizziness, palpitations, or syncope.', 'Metabolism, Synergism', 'Caution and clinical monitoring are recommended if a class IC antiarrhythmic agent is prescribed in combination with a tricyclic antidepressant.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481400, 'Troleandomycin', 'Propafenone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of propafenone, which may result in proarrhythmic events and exaggerated beta-adrenergic blocking activity.', 'DDInter', 1767317477, 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors. Therapeutic response to propafenone as well as clinical and electrocardiographic evidence of toxicity should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the propafenone dosage adjusted as necessary. Patients should be monitored for potential adverse effects such as new or worsening arrhythmias or heart failure, edema, bradycardia, and atrioventricular block. Simultaneous use of propafenone with both a CYP450 3A4 inhibitor and a CYP450 2D6 inhibitor (or in patients with CYP450 2D6 deficiency) should be avoided.', 'Metabolism', 'Caution is advised when propafenone is administered with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481401, 'Ubrogepant', 'Propafenone', 'Moderate', 'Coadministration with breast cancer resistance protein (BCRP) and/or P-glycoprotein (P-gp) only inhibitors may increase the exposure of ubrogepant, which is a substrate of these transporters.', 'DDInter', 1767317477, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors. If needed, a second 50 mg dose of ubrogepant may be administered at least 2 hours after the initial dose.', 'Distribution', 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
