INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481578, 'Propranolol', 'Kaolin', 'Minor', 'Concurrent administration with aluminum and magnesium antacids has been shown to decrease the oral bioavailability of certain beta-blockers, although data are conflicting. The exact mechanism of interaction is unknown but may involve cation binding of beta-blockers or a reduction in the dissolution rate due to increased gastric pH. Based on available data, the clinical significance of this potential interaction is difficult to determine. As a precaution, patients may want to consider separating the administration times of beta-blockers and antacids or other aluminum- or magnesium-containing products by at least 2 hours.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481579, 'Propranolol', 'Levobetaxolol (ophthalmic)', 'Moderate', 'Ophthalmic beta blocker solutions are systemically absorbed and can produce clinically significant systemic effects even at low or undetectable plasma levels. Additive effects of beta blockade may occur in patients receiving both oral and ophthalmic beta blocking agents.', 'DDInter', 1767317477, 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use. Patients should be advised to notify their physician if they experience a slow pulse, irregular heartbeat, dizziness, shortness of breath, or syncope.', 'Synergism', 'Blood pressure and intraocular pressure monitoring is recommended during concurrent use.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481580, 'Propranolol', 'Licorice', 'Moderate', 'Licorice use has been associated with hypertension and may antagonize the effects of antihypertensive agents. Glycyrrhizic acid, a component of licorice, is hydrolyzed in the intestine to a metabolite (glycyrrhetinic acid) that causes mineralocorticoid and renin-suppressing effects.', 'DDInter', 1767317477, 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products. Even relatively moderate doses of licorice may be problematic in susceptible patients when ingested regularly for prolonged periods.', 'Antagonism', 'Patients receiving antihypertensive therapy should avoid or limit the consumption of licorice-containing products.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481581, 'Propranolol', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C19 isoenzyme.', 'DDInter', 1767317477, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 2C19, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
