INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481614, 'Ranolazine', 'Propranolol', 'Moderate', 'Coadministration with ranolazine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Ranolazine has been shown in vitro to be an inhibitor of CYP450 2D6. However, concomitant use of ranolazine with other drugs that are metabolized by CYP450 2D6 such as tricyclic antidepressants and antipsychotics has not been studied.', 'DDInter', 1767317477, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ranolazine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
