INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481675, 'Zileuton', 'Propranolol', 'Moderate', 'Zileuton is believed to inhibit the hepatic metabolism of propranolol. Propranolol serum concentrations may increase by as much as 100% with a resultant increase in beta blockade.', 'DDInter', 1767317477, 'If zileuton and propranolol must be given concomitantly, the patient should be observed closely for altered propranolol effects and the dose adjusted accordingly. Patients should be advised to notify their physician if they experience syncope,slow pulse, irregular heartbeat, dizziness, or shortness of breath.', 'Metabolism', 'If zileuton and propranolol must be given concomitantly, the patient should be observed closely for altered propranolol effects and the dose adjusted accordingly.', NULL, NULL, 'Moderate', 'pharmacodynamic');
