INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1481677, 'Zolmitriptan', 'Propranolol', 'Minor', 'After one week of dosing with zolmitriptan and propranolol 160 mg/day, the Cmax and AUC of zolmitriptan increased 1.5 times while the Cmax and AUC of the N-desmethyl metabolite decreased by 30% and 15%, respectively. The mechanism of the interaction has not been described. It does not appear that any special precautions are necessary when coadministering zolmitriptan with beta-blockers, although further post-marketing studies will help to define this interaction.', 'DDInter', 1767317477, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
