        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1481987, 'Edrophonium', 'Pyridostigmine', 'Moderate', 'A synergistic effect may be expected when acetylcholinesterase inhibitors are administered concurrently with other cholinesterase inhibitors or cholinergic agonists.', NULL, 'Monitoring for excessive cholinergic effects and seizure activity is recommended.', NULL, 'Monitoring for excessive cholinergic effects and seizure activity is recommended. Patients should be advised to notify their physician if they experience nausea, vomiting, diarrhea, abdominal pain, increased salivation, cold sweats, slow or irregular heartbeat, muscle weakness or cramps, dizziness, headache, seizures, or respiratory distress.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
