INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482099, 'Quazepam', 'Mephenytoin', 'Moderate', 'Coadministration with some benzodiazepines may alter the serum concentrations of phenytoin. Phenytoin toxicity has been reported in patients treated with various benzodiazepines, including clobazam, chlordiazepoxide, clonazepam, and diazepam. Conversely, phenytoin may reduce the plasma concentrations of some benzodiazepines by inducing their metabolism via hepatic microsomal enzymes.', 'DDInter', 1767317477, 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary. Patients should be advised to contact their physician if they experience symptoms of hydantoin toxicity such as nausea, vomiting, tremors, ataxia, lethargy, slurred speech, visual disturbances, or changes in mental status. The potential for diminished or inadequate benzodiazepine effects should be considered during concomitant use with phenytoin.', 'Metabolism', 'Pharmacologic response and serum hydantoin levels should be monitored more closely whenever a benzodiazepine is added to or withdrawn from therapy, and the hydantoin dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482100, 'Quazepam', 'Meprobamate', 'Moderate', 'Central nervous system- and/or respiratory-depressant effects may be additively or synergistically increased in patients taking multiple drugs that cause these effects, especially in elderly or debilitated patients.', 'DDInter', 1767317477, 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression. Cautious dosage titration may be required, particularly at treatment initiation. Ambulatory patients should be counseled to avoid hazardous activities requiring mental alertness and motor coordination.', 'Synergism', 'During concomitant use of these drugs, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', NULL, NULL, 'Moderate', 'pharmacodynamic');
