INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482306, 'Imatinib', 'Quetiapine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of quetiapine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to quetiapine should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the quetiapine dosage adjusted as necessary. Patients should be monitored for potentially increased adverse effects such as dizziness, drowsiness, dry mouth, constipation, increased appetite, weight gain, extrapyramidal symptoms, tardive dyskinesia, hyperglycemia, dyslipidemia, hyperprolactinemia (galactorrhea, amenorrhea, gynecomastia), orthostatic hypotension, blood pressure increases (in children and adolescents), QT prolongation, cognitive and motor impairment, dysphagia, and heat-related illnesses due to disruption of body temperature regulation.', 'Metabolism', 'Pharmacologic response to quetiapine should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the quetiapine dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
