INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482308, 'Indacaterol', 'Quetiapine', 'Moderate', 'Beta-2 adrenergic agonists can cause dose-related prolongation of the QT interval and potassium loss. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Clinically significant prolongation of QT interval and hypokalemia occur infrequently when beta-2 agonists are inhaled at normally recommended dosages. However, these effects may be more common when the drugs are administered systemically or when recommended dosages are exceeded.', 'DDInter', 1767317477, 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is recommended if beta-2 agonists are used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482309, 'Indapamide', 'Quetiapine', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317477, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482310, 'Indinavir', 'Quetiapine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of quetiapine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. The product labeling recommends a reduction to one-sixth of the original dosage. Following discontinuation of the CYP450 3A4 inhibitor, the dosage of quetiapine should be increased by 6-fold.', 'Metabolism', 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482311, 'Insulin human', 'Quetiapine', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
