INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482406, 'Telaprevir', 'Quetiapine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of quetiapine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics. The product labeling recommends a reduction to one-sixth of the original dosage. Following discontinuation of the CYP450 3A4 inhibitor, the dosage of quetiapine should be increased by 6-fold.', 'Metabolism', 'The dosage of quetiapine should be reduced when prescribed with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, NULL, 'Major', 'pharmacodynamic');
