INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482703, 'Quinine', 'Pancuronium', 'Moderate', 'Quinine may enhance the effects of neuromuscular blockers. Dyspnea and recurarisation have been reported in a patient who received gentamicin and penicillin before surgery, succinylcholine and pancuronium intraoperatively, and intravenous quinine after reversal of neuromuscular blockade. The mechanism is not unknown; however, the neuromuscular blocking effects of gentamicin may have also been involved.', 'DDInter', 1767317477, 'Patients should be closely monitored for prolonged or excessive neuromuscular blockade and treated appropriately.', 'Synergism', 'Patients should be closely monitored for prolonged or excessive neuromuscular blockade and treated appropriately.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482704, 'Quinine', 'Papaverine', 'Major', 'Intracoronary administration of papaverine has been associated with QT interval prolongation and torsade de pointes (TdP) arrhythmia. The risk may theoretically increase in patients receiving concomitant medications that can also prolong the QT interval or cause bradycardia. The precise mechanism of papaverine-induced ventricular tachyarrhythmias has not been delineated, but may involve inhibition of potassium currents and prolongation of the action potential duration.', 'DDInter', 1767317477, 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', 'Synergism', 'Caution and close monitoring are advised during intracoronary administration of papaverine, particularly in patients receiving concomitant drugs that can prolong the QT interval or cause bradycardia and in patients with other risk factors described above.', NULL, NULL, 'Major', 'pharmacodynamic');
