INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482788, 'Ziprasidone', 'Quinine', 'Major', 'Ziprasidone can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. Excessive parasympatholytic effects may include paralytic ileus, hyperthermia, mydriasis, blurred vision, tachycardia, urinary retention, psychosis, and seizures.', 'DDInter', 1767317477, 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', 'Synergism', 'Coadministration of ziprasidone with other drugs that can prolong the QT interval is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482789, 'Aluminum hydroxide', 'Raltegravir', 'Major', 'Coadministration with aluminum-, magnesium-, and/or calcium-containing antacids may reduce the oral bioavailability of raltegravir. The proposed mechanism is chelation of raltegravir by polyvalent cations, but changes in solubility and lipophilicity of raltegravir related to pH increases may also contribute.', 'DDInter', 1767317477, 'Concomitant use of raltegravir with aluminum- and/or magnesium-containing antacids should generally be avoided, even if administration is staggered. Antacids containing calcium carbonate should not be used with once-daily raltegravir (HD formulation), but may be used without dose adjustment for other raltegravir products.', 'Absorption', 'Concomitant use of raltegravir with aluminum- and/or magnesium-containing antacids should generally be avoided, even if administration is staggered.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482790, 'Amprenavir', 'Raltegravir', 'Moderate', 'Coadministration of fosamprenavir (with or without ritonavir) and raltegravir may decrease the plasma concentrations of both amprenavir and raltegravir. The mechanism of interaction has not been described.', 'DDInter', 1767317477, 'Caution is advised if fosamprenavir is prescribed in combination with raltegravir. Appropriate dosages of the combination with respect to safety and efficacy have not been established.', 'Others', 'Caution is advised if fosamprenavir is prescribed in combination with raltegravir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
