INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482793, 'Esomeprazole', 'Raltegravir', 'Minor', 'Since raltegravir solubility is increased at higher pH, coadministration with agents that elevate gastric pH may theoretically increase the plasma concentrations of raltegravir. However, concurrent use of proton pump inhibitors or H2-receptor antagonists in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with proton pump inhibitors or H2-receptor antagonists.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482794, 'Etravirine', 'Raltegravir', 'Minor', 'Coadministration with etravirine may reduce the plasma concentrations of raltegravir. The mechanism of interaction has not been described. Raltegravir had no effect on the pharmacokinetics of etravirine. Raltegravir and etravirine may be coadministered without any dosage adjustments.', 'DDInter', 1767317477, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
