INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482796, 'Fosphenytoin', 'Raltegravir', 'Minor', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) 1A1 may reduce the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. The impact of other potent inducers of drug-metabolizing enzymes such as carbamazepine, phenytoin, and phenobarbital on UGT1A1 is unknown. Other, less potent inducers (e.g., efavirenz, nevirapine, etravirine, rifabutin, dexamethasone, St. John''s Wort, pioglitazone) may be used with the recommended dosage of raltegravir.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
