INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482799, 'Raltegravir', 'Magnesium citrate', 'Moderate', 'Coadministration with aluminum- and magnesium-containing antacids has been shown to reduce the oral bioavailability of raltegravir. The proposed mechanism is chelation of raltegravir by polyvalent cations, but changes in solubility and lipophilicity of raltegravir related to pH increases may also contribute.', 'DDInter', 1767317477, 'It is not known to what extent non-antacid aluminum and magnesium salts may interact with raltegravir. Until more information is available, it may be advisable to avoid taking raltegravir with any aluminum- and/or magnesium-containing product. According to the manufacturer, antacids containing calcium carbonate may be used without dose adjustment for raltegravir.', 'Absorption', 'It is not known to what extent non-antacid aluminum and magnesium salts may interact with raltegravir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
