INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482803, 'Raltegravir', 'Phenobarbital', 'Minor', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) 1A1 may reduce the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. The impact of other potent inducers of drug-metabolizing enzymes such as carbamazepine, phenytoin, and phenobarbital on UGT1A1 is unknown. Other, less potent inducers (e.g., efavirenz, nevirapine, etravirine, rifabutin, dexamethasone, St. John''s Wort, pioglitazone) may be used with the recommended dosage of raltegravir.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482804, 'Ranitidine (bismuth citrate)', 'Raltegravir', 'Minor', 'Since raltegravir solubility is increased at higher pH, coadministration with agents that elevate gastric pH may theoretically increase the plasma concentrations of raltegravir. However, concurrent use of proton pump inhibitors or H2-receptor antagonists in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with proton pump inhibitors or H2-receptor antagonists.', 'DDInter', 1767317477, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482805, 'Rifabutin', 'Raltegravir', 'Minor', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) 1A1 may reduce the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. The impact of other potent inducers of drug-metabolizing enzymes such as carbamazepine, phenytoin, and phenobarbital on UGT1A1 is unknown. Other, less potent inducers (e.g., efavirenz, nevirapine, etravirine, rifabutin, dexamethasone, St. John''s Wort, pioglitazone) may be used with the recommended dosage of raltegravir.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482806, 'Rifampicin', 'Raltegravir', 'Major', 'Coadministration with rifampin may significantly decrease the plasma concentrations of raltegravir. The proposed mechanism is rifampin induction of raltegravir metabolism via uridine diphosphate glucuronosyltransferase (UGT) 1A1.', 'DDInter', 1767317477, 'Caution is advised when raltegravir must be used in combination with rifampin. A dosage increase for raltegravir to 800 mg twice daily is recommended for adults during coadministration with rifampin. No dosage adjustment recommendations are available for pediatric patients below 18 years of age due to the lack of clinical data.', 'Metabolism', 'Caution is advised when raltegravir must be used in combination with rifampin.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482807, 'Rifapentine', 'Raltegravir', 'Minor', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) 1A1 may reduce the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. The impact of other potent inducers of drug-metabolizing enzymes such as carbamazepine, phenytoin, and phenobarbital on UGT1A1 is unknown. Other, less potent inducers (e.g., efavirenz, nevirapine, etravirine, rifabutin, dexamethasone, St. John''s Wort, pioglitazone) may be used with the recommended dosage of raltegravir.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482808, 'Rucaparib', 'Raltegravir', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of breast cancer resistance protein (BCRP) (e.g., rosuvastatin) and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 (e.g., deferasirox).', 'DDInter', 1767317477, 'Caution is advised if rucaparib is used with drugs that are substrates of BCRP and/or UGT1A1, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if rucaparib is used with drugs that are substrates of BCRP and/or UGT1A1, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482809, 'Sodium zirconium cyclosilicate', 'Raltegravir', 'Moderate', 'INTERVAL: Because sodium zirconium cyclosilicate can transiently increase gastric pH, it may affect the absorption of coadministered medications that exhibit pH-dependent solubility. Altered efficacy or safety of these medications may occur when they are administered too close to the dosing of sodium zirconium cyclosilicate.', 'DDInter', 1767317477, 'In general, concomitant oral medications should be administered at least 2 hours before or 2 hours after sodium zirconium cyclosilicate. Separation of dosing times is not needed if it has been determined that the concomitant medication does not exhibit pH-dependent solubility.', 'Absorption', 'In general, concomitant oral medications should be administered at least 2 hours before or 2 hours after sodium zirconium cyclosilicate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482810, 'Sorafenib', 'Raltegravir', 'Minor', 'Coadministration with inhibitors of uridine diphosphate glucuronosyltransferase (UGT) 1A1 such as atazanavir, nilotinib, or sorafenib may increase the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. However, concurrent use of atazanavir/ritonavir in clinical trials did not result in meaningful differences in the safety of raltegravir. Based on these data, no dosage adjustment of raltegravir is necessary when used together with atazanavir/ritonavir or other UGT1A1 inhibitors.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482811, 'St. John''s Wort', 'Raltegravir', 'Minor', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase (UGT) 1A1 may reduce the plasma concentrations of raltegravir, which is primarily metabolized by UGT1A1-mediated glucuronidation. The impact of other potent inducers of drug-metabolizing enzymes such as carbamazepine, phenytoin, and phenobarbital on UGT1A1 is unknown. Other, less potent inducers (e.g., efavirenz, nevirapine, etravirine, rifabutin, dexamethasone, St. John''s Wort, pioglitazone) may be used with the recommended dosage of raltegravir.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482812, 'Tafamidis', 'Raltegravir', 'Moderate', 'Based on in vitro data, coadministration with tafamidis may increase the plasma concentrations and the risk of toxicity of drugs that are substrates of the transport protein breast cancer resistance protein (BCRP) (e.g., methotrexate, rosuvastatin, imatinib), uridine diphosphate glucuronosyltransferase (UGT) 1A1 (e.g., bictegravir, irinotecan, raltegravir), and/or uptake transporters OAT1 and OAT3 (organic anion transporters) (e.g., bumetanide, furosemide, lamivudine, methotrexate, oseltamivir, tenofovir, ganciclovir, adefovir, cidofovir, zidovudine, zalcitabine) at clinically relevant concentrations. The proposed mechanism is decreased clearance due to tafamidis-mediated inhibition of the corresponding transport protein.', 'DDInter', 1767317477, 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever tafamidis is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if tafamidis is used concomitantly with drugs that are substrates of these transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482813, 'Telaprevir', 'Raltegravir', 'Minor', 'Coadministration with telaprevir may modestly increase the plasma concentrations of raltegravir. The mechanism of interaction has not been established. Raltegravir did not have a clinically meaningful effect on the pharmacokinetics of telaprevir. No dosage adjustment is required for raltegravir or telaprevir during coadministration.', 'DDInter', 1767317477, '-', 'Others', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482814, 'Tipranavir', 'Raltegravir', 'Minor', 'Coadministration with tipranavir/ritonavir may reduce the plasma concentrations of raltegravir. The proposed mechanism is induction of raltegravir glucuronidation via uridine diphosphate glucuronosyltransferase (UGT) 1A1.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
