INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482889, 'Ramelteon', 'Morphine (liposomal)', 'Major', 'Concomitant use of opioids with benzodiazepines or other central nervous system (CNS) depressants (e.g., nonbenzodiazepine sedatives/hypnotics, anxiolytics, muscle relaxants, general anesthetics, antipsychotics, other opioids, alcohol) may result in profound sedation, respiratory depression, coma, and death. The risk of hypotension may also be increased with some CNS depressants (e.g., alcohol, benzodiazepines, phenothiazines).', 'DDInter', 1767317477, 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate. If coadministration is necessary, the dosage and duration of each drug should be limited to the minimum required to achieve desired clinical effect, with cautious titration and dosage adjustments when needed. Patients should be monitored closely for signs and symptoms of respiratory depression and sedation, and advised to avoid driving or operating hazardous machinery.', 'Synergism', 'The use of opioids in conjunction with benzodiazepines or other CNS depressants should generally be avoided unless alternative treatment options are inadequate.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1482890, 'Ramelteon', 'Nafcillin', 'Moderate', 'Coadministration with inducers of CYP450 isoenzymes may decrease the plasma concentrations and pharmacologic effects of ramelteon, which is metabolized by CYP450 1A2 and, to a lesser extent, by CYP450 3A4 and the 2C subfamily of isoenzymes.', 'DDInter', 1767317477, 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St. John''s wort. Other known inducers include aminoglutethimide, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, mitotane, modafinil, nafcillin, nevirapine, rifabutin, rifapentine, barbiturates and various other anticonvulsants, although the extent to which they interact with ramelteon is unknown.', 'Metabolism', 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
