INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483181, 'Abarelix', 'Ranolazine', 'Moderate', 'Long-term androgen deprivation therapy can prolong the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death. In addition, the extent of drug-induced QT prolongation is dependent on the particular drug(s) involved and dosage(s) of the drug(s).', 'DDInter', 1767317477, 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval. Electrolyte abnormalities should be corrected prior to initiating therapy, and periodic monitoring of electrocardiograms and electrolytes should be considered.', 'Synergism', 'The benefits of androgen deprivation therapy should be carefully assessed against the potential risk in patients receiving other drugs known to prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483182, 'Aminoglutethimide', 'Ranolazine', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The extent to which other, less potent CYP450 3A4 inducers may interact with ranolazine is unknown. Caution is advised if they are used with ranolazine.', 'Metabolism', 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483183, 'Amoxapine', 'Ranolazine', 'Moderate', 'Ranolazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of ranolazine, the maximum recommended dosage of 1000 mg twice daily should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Ranolazine is contraindicated in patients with liver cirrhosis because of the profound effect on QT prolongation in this population.', 'Synergism', 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483184, 'Amprenavir', 'Ranolazine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme. Because ranolazine prolongs QT interval in a dose-dependent manner, high plasma levels of ranolazine may increase the risk of ventricular arrhythmias such as ventricular tachycardia, ventricular fibrillation, and torsade de pointes.', 'DDInter', 1767317477, 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of ranolazine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of ranolazine with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
