INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483263, 'Irinotecan (liposomal)', 'Ranolazine', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or UGT1A1 may increase the plasma concentrations of irinotecan and its active metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 1767317477, 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors. Patients should be monitored for toxicities such as diarrhea, myelosuppression, thromboembolism, and interstitial lung disease, and the irinotecan dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Caution is advised when irinotecan is prescribed with CYP450 3A4 or UGT1A1 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
