INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483343, 'Timolol', 'Ranolazine', 'Moderate', 'Coadministration with ranolazine may increase the plasma concentrations of drugs that are substrates of the CYP450 2D6 isoenzyme. Ranolazine has been shown in vitro to be an inhibitor of CYP450 2D6. However, concomitant use of ranolazine with other drugs that are metabolized by CYP450 2D6 such as tricyclic antidepressants and antipsychotics has not been studied.', 'DDInter', 1767317477, 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ranolazine is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if ranolazine must be used concurrently with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483344, 'Tizanidine', 'Ranolazine', 'Moderate', 'Ranolazine can cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval. Since the magnitude of QTc prolongation increases with increasing plasma concentrations of ranolazine, the maximum recommended dosage of 1000 mg twice daily should not be exceeded. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope. Ranolazine is contraindicated in patients with liver cirrhosis because of the profound effect on QT prolongation in this population.', 'Synergism', 'Caution is recommended if ranolazine is used in combination with other drugs that can prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483345, 'Tolterodine', 'Ranolazine', 'Minor', 'Coadministration with drugs that are inhibitors of CYP450 2D6 may increase the plasma concentrations of tolterodine, which is primarily metabolized by this isoenzyme in patients that are extensive metabolizers, or EMs (approximately 93% of Caucasians and more than 98% of Asians and individuals of African descent). Moreover, since the active 5-hydroxymethyl metabolite (5-HM) generated by CYP450 2D6 is equipotent compared with tolterodine, and the sum of unbound serum concentrations of tolterodine and 5-HM are only 25% higher during the interaction, little alteration in the overall pharmacologic activity would be expected. Thus, dosage reductions should not be necessary.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483346, 'Toremifene', 'Ranolazine', 'Major', 'Toremifene has the potential to prolong the QT interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that can prolong the QT interval may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided. If treatment with other QT-prolonging drugs is required, interruption of toremifene therapy should be considered. Caution and clinical monitoring are recommended if concomitant use is unavoidable. In patients at increased risk, electrocardiograms (ECGs) are recommended at baseline and as clinically indicated. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Coadministration of toremifene with other drugs that can prolong the QT interval should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
