INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483383, 'Berotralstat', 'Sirolimus', 'Moderate', 'Coadministration with drugs that are inhibitors of CYP450 3A4 may increase the blood concentrations of the macrolide immunosuppressants sirolimus and tacrolimus, both of which are metabolized by the isoenzyme.', 'DDInter', 1767317477, 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of macrolide immunosuppressant therapy should be considered, including adverse effects such as fever, infection, diarrhea, hypokalemia, anemia, thrombocytopenia, leukopenia, and hyperlipidemia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483384, 'Bifidobacterium longum infantis', 'Sirolimus', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy. It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
