INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483388, 'Candida albicans', 'Sirolimus', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483389, 'Cerivastatin', 'Sirolimus', 'Moderate', 'Coadministration of a macrolide immunosuppressant with certain HMG-CoA reductase inhibitors may result in elevated plasma concentrations of both due to competitive inhibition of CYP450 3A4 metabolism. High levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity including rhabdomyolysis, which can be fatal.', 'DDInter', 1767317477, 'In general, lovastatin, red yeast rice (which contains lovastatin), and simvastatin should preferably be avoided in patients treated with sirolimus or tacrolimus due to the potential for serious interaction. Atorvastatin may be used with caution, although the dosage should start low, and immunosuppressant blood levels should be closely monitored. Pravastatin and fluvastatin are probably the safest alternatives, since they are not metabolized by CYP450 3A4. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'In general, lovastatin, red yeast rice (which contains lovastatin), and simvastatin should preferably be avoided in patients treated with sirolimus or tacrolimus due to the potential for serious interaction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483390, 'Vibrio cholerae CVD 103-HgR strain live antigen', 'Sirolimus', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483391, 'Cisapride', 'Sirolimus', 'Moderate', 'Coadministration with gastrointestinal prokinetic agents such as cisapride, domperidone, and metoclopramide may increase the oral bioavailability of macrolide immunosuppressants. Because macrolide immunosuppressants are incompletely and variably absorbed following oral administration, increased gastrointestinal motility and gastric emptying can enhance delivery of the drugs to the small intestine where they are absorbed.', 'DDInter', 1767317477, 'Pharmacologic response, renal function, and macrolide immunosuppressant blood levels should be monitored more closely whenever a gastrointestinal prokinetic agent is added to or discontinue from therapy, and the immunosuppressant dosage adjusted as necessary. Patients should be advised to contact their physician if they experience potential signs and symptoms of macrolide immunosuppressant toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, or lethargy.', 'Absorption', 'Pharmacologic response, renal function, and macrolide immunosuppressant blood levels should be monitored more closely whenever a gastrointestinal prokinetic agent is added to or discontinue from therapy, and the immunosuppressant dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483392, 'Clofibrate', 'Sirolimus', 'Moderate', 'Higher dosages of lipid-lowering agents may be required during concomitant administration of sirolimus or temsirolimus. Dose-related serum lipid abnormalities, including but not limited to increased serum cholesterol and triglycerides, have been frequently reported in patients treated with sirolimus or temsirolimus.', 'DDInter', 1767317477, 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents. Modification of the antihyperlipidemic regimen or dosages may be necessary.', 'Others', 'Close clinical monitoring of serum cholesterol and triglycerides is recommended if sirolimus or temsirolimus is prescribed to patients receiving lipid-lowering agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483393, 'Clotrimazole', 'Sirolimus', 'Major', 'Coadministration with clotrimazole may significantly increase the oral bioavailability of sirolimus and tacrolimus. The proposed mechanism is clotrimazole inhibition of sirolimus and tacrolimus metabolism via intestinal CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised if sirolimus or tacrolimus is prescribed with clotrimazole. Immunosuppressant blood levels should be closely monitored and the dosage adjusted accordingly, particularly following initiation, discontinuation, or change of dosage of clotrimazole in patients who are stabilized on their antirejection regimen.', 'Metabolism', 'Caution is advised if sirolimus or tacrolimus is prescribed with clotrimazole.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483394, 'Coccidioides immitis spherule', 'Sirolimus', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
