INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483465, 'Ibrutinib', 'Sirolimus', 'Moderate', 'Coadministration with ibrutinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter. In vitro studies indicate that ibrutinib is not a substrate for P-gp, and systemic ibrutinib is unlikely to be an inhibitor of P-gp at clinical doses.', 'DDInter', 1767317477, 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range. Dosage reduction and/or dosage interval adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ibrutinib is added to or withdrawn from therapy. Some authorities recommend that narrow therapeutic range P-gp substrates, such as digoxin, should be administered at least 6 hours before or after ibrutinib.', 'Absorption', 'Caution is advised if ibrutinib is prescribed in combination with orally administered P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483466, 'Idelalisib', 'Sirolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly increase the plasma concentrations of sirolimus following oral administration. Sirolimus is a substrate of both CYP450 3A4 isoenzyme and P-gp efflux transporter, thus their inhibition in the intestine can enhance the absorption of sirolimus.', 'DDInter', 1767317477, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided. The manufacturers of posaconazole and voriconazole consider coadministration with sirolimus to be contraindicated. Some authorities recommend avoiding concomitant use of sirolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483467, 'Ifosfamide', 'Sirolimus', 'Moderate', 'Coadministration of sirolimus or tacrolimus with other drugs that are also metabolized by CYP450 3A4 may result in elevated plasma concentrations of the macrolide immunosuppressant and/or the coadministered drug(s). The mechanism is decreased drug clearance due to competitive inhibition of CYP450 3A4 activity.', 'DDInter', 1767317477, 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', 'Metabolism', 'Pharmacologic responses and/or plasma drug levels should be monitored more closely whenever a macrolide immunosuppressant or another substrate of CYP450 3A4 is added to or withdrawn from therapy, and the dosage(s) adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483468, 'Imatinib', 'Sirolimus', 'Moderate', 'Coadministration with imatinib may increase the plasma concentrations of drugs that are substrates of CYP450 2C9, 2D6 and/or 3A4. The mechanism is decreased clearance due to inhibition of these isoenzymes by imatinib.', 'DDInter', 1767317477, 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever imatinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when imatinib is used concomitantly with drugs that undergo metabolism by CYP450 2C9, 2D6 and/or 3A4, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
