INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483490, 'Sirolimus', 'Lomitapide', 'Moderate', 'Coadministration with lomitapide may increase the plasma concentrations of drugs that are substrates of P-glycoprotein. The proposed mechanism is decreased clearance in the intestine and/or liver due to inhibition of P-glycoprotein-mediated drug efflux by lomitapide.', 'DDInter', 1767317477, 'Caution is advised if lomitapide must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lomitapide is added to or withdrawn from therapy.', 'Absorption', 'Caution is advised if lomitapide must be used concurrently with medications that are substrates of the P-glycoprotein efflux transporter, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
