INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483511, 'Sirolimus', 'Rifampicin', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein may significantly decrease the plasma concentrations and pharmacologic effects of sirolimus. The mechanism probably involves reduced absorption as well as accelerated clearance of sirolimus due to induction of both intestinal P-glycoprotein drug efflux transporter and hepatic/intestinal CYP450 3A4 isoenzymes.', 'DDInter', 1767317477, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended. Alternative therapeutic agents with less enzyme induction potential should be considered.', 'Metabolism', 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
