INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483552, 'Telaprevir', 'Sirolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly increase the plasma concentrations of sirolimus following oral administration. Sirolimus is a substrate of both CYP450 3A4 isoenzyme and P-gp efflux transporter, thus their inhibition in the intestine can enhance the absorption of sirolimus.', 'DDInter', 1767317477, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided. The manufacturers of posaconazole and voriconazole consider coadministration with sirolimus to be contraindicated. Some authorities recommend avoiding concomitant use of sirolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
