INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483574, 'Trichophyton mentagrophytes', 'Sirolimus', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483575, 'Troglitazone', 'Sirolimus', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sirolimus and tacrolimus, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers. Sirolimus and tacrolimus blood levels should be checked frequently and the dosage adjusted accordingly whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483576, 'Troleandomycin', 'Sirolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly increase the plasma concentrations of sirolimus following oral administration. Sirolimus is a substrate of both CYP450 3A4 isoenzyme and P-gp efflux transporter, thus their inhibition in the intestine can enhance the absorption of sirolimus.', 'DDInter', 1767317477, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided. The manufacturers of posaconazole and voriconazole consider coadministration with sirolimus to be contraindicated. Some authorities recommend avoiding concomitant use of sirolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483577, 'Tuberculin purified protein derivative', 'Sirolimus', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483578, 'Tucatinib', 'Sirolimus', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly increase the plasma concentrations of sirolimus following oral administration. Sirolimus is a substrate of both CYP450 3A4 isoenzyme and P-gp efflux transporter, thus their inhibition in the intestine can enhance the absorption of sirolimus.', 'DDInter', 1767317477, 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided. The manufacturers of posaconazole and voriconazole consider coadministration with sirolimus to be contraindicated. Some authorities recommend avoiding concomitant use of sirolimus during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of sirolimus with potent CYP450 3A4 and/or P-gp inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483579, 'Typhoid vaccine (inactivated)', 'Sirolimus', 'Moderate', 'The administration of inactivated, killed, or otherwise noninfectious vaccines to immunosuppressed patients is generally safe but may be associated with a diminished or suboptimal immunologic response due to antibody inhibition. Such patients may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317477, 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients. For Haemophilus influenzae b vaccine, some experts recommend that it be administered at least 2 weeks before starting or 3 months after discontinuing chemotherapy when used in patients with Hodgkin''s disease. For rabies vaccine, some authorities suggest that immunosuppressive agents should generally be avoided during postexposure therapy except when absolutely necessary for the treatment of other conditions.', 'Antagonism', 'In general, the US Department of Public Health Advisory Committee on Immunization Practices (ACIP) recommends that inactivated or killed vaccines be administered to non-HIV immunosuppressed patients according to the same guidelines as for healthy patients.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483580, 'Typhoid vaccine (live)', 'Sirolimus', 'Major', 'The administration of live, attenuated viral or bacterial vaccines during immunosuppressant or intense antineoplastic therapy may be associated with a risk of disseminated infection due to enhanced replication of vaccine virus or bacteria in the presence of diminished immune competence. Patients may be immunosuppressed if they have recently received or are receiving alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids. These patients may also have increased adverse reactions and decreased or suboptimal immunologic response to vaccines.', 'DDInter', 1767317477, 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy. Vaccination should be deferred until after such therapy is discontinued and immune function has been restored, usually 4 to 12 weeks after stopping immunosuppressive therapy. A longer waiting period may be necessary following treatment with agents that have a prolonged elimination half-life (e.g., leflunomide, teriflunomide). Current local immunization guidelines should be consulted for recommendations.', 'Others', 'In general, live attenuated vaccines should not be used in patients receiving immunosuppressive therapy or cancer chemotherapy.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483581, 'Ulipristal', 'Sirolimus', 'Moderate', 'Based on in vitro inhibition data, coadministration with ulipristal may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by ulipristal.', 'DDInter', 1767317477, 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever ulipristal is added to or withdrawn from therapy.', 'Others', 'Caution is advised when ulipristal is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483582, 'Upadacitinib', 'Sirolimus', 'Major', 'Coadministration of upadacitinib with other immunosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving upadacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids. The most common serious infections reported with upadacitinib treatment include pneumonia and cellulitis. Opportunistic infections include tuberculosis, multidermatomal herpes zoster, oral/esophageal candidiasis, and cryptococcosis. Lymphoma and other malignancies, including non-melanoma skin cancers, have also been observed with the use of upadacitinib and other Janus kinase (JAK) inhibitors.', 'DDInter', 1767317477, 'Concomitant use of upadacitinib with other JAK inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.g., azathioprine, cyclosporine) is not recommended. Patients receiving upadacitinib should be closely monitored for the development of signs and symptoms of infection during and after treatment, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. If a serious or opportunistic infection develops, upadacitinib should be interrupted until the infection is controlled.', 'Others', 'Concomitant use of upadacitinib with other JAK inhibitors, biologic disease-modifying antirheumatic drugs (DMARDs), or potent immunosuppressants (e.', NULL, NULL, 'Major', 'pharmacodynamic');
