INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483593, 'Voriconazole', 'Sirolimus', 'Major', 'Coadministration with voriconazole may significantly increase the plasma concentrations of sirolimus. The mechanism is voriconazole inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of sirolimus.', 'DDInter', 1767317477, 'The use of sirolimus with voriconazole is considered contraindicated.', 'Metabolism', 'The use of sirolimus with voriconazole is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
