INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483695, 'Rasagiline', 'Levmetamfetamine (nasal)', 'Moderate', 'By inhibiting the breakdown of catecholamines, monoamine oxidase inhibitors (MAOIs) may theoretically potentiate the cardiovascular effects of systemically absorbed sympathomimetic agents following topical administration (e.g., nasal decongestants, ophthalmic vasoconstrictors).', 'DDInter', 1767317477, 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).', 'Synergism', 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483696, 'Rasagiline', 'Levobetaxolol (ophthalmic)', 'Moderate', 'Sympathetic ganglion-blocking or catecholamine-depleting agents such as guanethidine, reserpine, and monoamine oxidase (MAO) inhibitors may potentiate the pharmacologic effects of beta-blockers, which are thought to competitively antagonize catecholamines at cardiac and other peripheral adrenergic neurons. Combining these medications may increase the risk of hypotension, orthostasis, bradycardia, and heart failure due to excessive reduction of sympathetic activity.', 'DDInter', 1767317477, 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents. Patients should contact their doctor if they experience dizziness, lightheadedness, syncope, bradycardia, shortness of breath, difficulty breathing, edema, and/or chest pain.', 'Synergism', 'Caution is advised if beta-blockers, including ophthalmic formulations, are prescribed in combination with sympathetic ganglion-blocking or catecholamine-depleting agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
