INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483748, 'Solriamfetol', 'Rasagiline', 'Major', 'Coadministration of solriamfetol with other drugs that also inhibit the reuptake of dopamine or norepinephrine, including monoamine oxidase inhibitors (MAOIs), may increase the risk of hypertensive reactions including death, stroke, myocardial infarction, aortic dissection, pulmonary edema, and renal failure. Additionally, MAOIs can increase dopaminergic and noradrenergic activity by inhibiting the metabolism of dopamine and norepinephrine.', 'DDInter', 1767317477, 'Concomitant use of solriamfetol with MAOIs is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with solriamfetol.', 'Metabolism, Synergism', 'Concomitant use of solriamfetol with MAOIs is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
