INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483761, 'Tetrabenazine', 'Rasagiline', 'Major', 'Tetrabenazine may cause excitation and hypertension in patients treated with monoamine oxidase inhibitors (MAOIs). The proposed mechanism has not been fully established but may be related to interference with biogenic amine storage. In addition, tetrabenazine may have depressant effects and may exacerbate depression.', 'DDInter', 1767317477, 'Concurrent administration of tetrabenazine and deutetrabenazine with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine) is considered contraindicated. At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with either tetrabenazine or deutetrabenazine, and vice versa.', 'Synergism', 'Concurrent administration of tetrabenazine and deutetrabenazine with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483762, 'Tetryzoline (nasal)', 'Rasagiline', 'Moderate', 'By inhibiting the breakdown of catecholamines, monoamine oxidase inhibitors (MAOIs) may theoretically potentiate the cardiovascular effects of systemically absorbed sympathomimetic agents following topical administration (e.g., nasal decongestants, ophthalmic vasoconstrictors).', 'DDInter', 1767317477, 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, linezolid, methylene blue, procarbazine).', 'Synergism', 'Although data are limited, caution may be advisable when topical decongestants and vasoconstrictors are coadministered with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
