INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483794, 'Tryptophan', 'Rasagiline', 'Major', 'By inhibiting serotonin metabolism, monoamine oxidase inhibitors (MAOIs) may potentiate the pharmacologic activity of serotonergic agents such as serotonin reuptake inhibitors, 5-HT1 receptor agonists, ergot alkaloids, buspirone, dextromethorphan, and most antidepressants. The result may be an increased risk of serotonin syndrome, which is a rare but serious and potentially fatal condition thought to result from hyperstimulation of brainstem 5-HT1A and 2A receptors.', 'DDInter', 1767317477, 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.g., furazolidone, methylene blue, procarbazine). At least 14 days should elapse between discontinuation of MAOI therapy and initiation of treatment with serotonergic agents.', 'Synergism', 'In general, serotonergic agents should not be used concurrently with MAOIs or other agents that possess MAOI activity (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483795, 'Valbenazine', 'Rasagiline', 'Major', 'Coadministration of valbenazine with monoamine oxidase inhibitors (MAOIs) may increase the risk of adverse effects such as serotonin syndrome or reduce the therapeutic effects of valbenazine. The mechanism for this interaction has not been delineated; however, concomitant use of these drugs may increase the concentration of monoamine neurotransmitters in synapses.', 'DDInter', 1767317477, 'The use of valbenazine with MAOIs should generally be avoided. If concomitant use is required, caution and close clinical and laboratory monitoring are recommended. Alternative treatment may be required if an interaction is suspected.', 'Others', 'The use of valbenazine with MAOIs should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
