INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483797, 'Vemurafenib', 'Rasagiline', 'Major', 'Coadministration with inhibitors of CYP450 1A2 may increase the plasma concentrations of rasagiline, which is primarily metabolized by the isoenzyme. Because the MAO-B selectivity of rasagiline may diminish with increasing dosage above the recommended range of 0.5 to 1 mg/day, there may be an increased risk of hypertensive crisis and other adverse reactions associated with nonselective inhibition of MAO if the maximum dosage is used in the presence of ciprofloxacin or other CYP450 1A2 inhibitors.', 'DDInter', 1767317477, 'Rasagiline should be prescribed at a maximum dosage of 0.5 mg/day during coadministration with moderate or potent CYP450 1A2 inhibitors. Patients should be advised to promptly seek medical attention if they experience potential signs and symptoms of a hypertensive crisis such as severe headache, visual disturbances, confusion, stupor or coma, seizures, chest pain, unexplained nausea or vomiting, and stroke-like symptoms.', 'Metabolism', 'Rasagiline should be prescribed at a maximum dosage of 0.', NULL, NULL, 'Major', 'pharmacodynamic');
