INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1483816, 'Belinostat', 'Regorafenib', 'Moderate', 'Coadministration with inhibitors of UGT1A1 may increase the plasma concentrations of belinostat, which is primarily (80% to 90%) metabolized by UGT1A1-mediated glucuronidation.', 'DDInter', 1767317477, 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided. Caution is advised when used with other agents that may also inhibit UGT1A1 such as nilotinib, pazopanib, regorafenib and sorafenib. Patients should be monitored closely for potentially increased adverse effects such as nausea, vomiting, diarrhea, thrombocytopenia, leukopenia (neutropenia and lymphopenia), anemia, infection, and hepatotoxicity.', 'Metabolism', 'Concomitant use of belinostat with potent UGT1A1 inhibitors such as atazanavir should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
