INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484357, 'Fosamprenavir', 'Repaglinide', 'Moderate', 'Coadministration with protease inhibitors may alter the hypoglycemic effect of repaglinide. Pharmacodynamically, protease inhibitors may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes. However, since they are potent inhibitors of CYP450 3A4, they may also increase the plasma concentrations and pharmacologic effects of repaglinide, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Pharmacologic response to repaglinide should be monitored more closely whenever a protease inhibitor is added to or withdrawn from therapy. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations. The repaglinide dosage may require adjustment if an interaction is suspected. Use of repaglinide with the fixed combination of atazanavir-cobicistat is not recommended.', 'Metabolism', 'Pharmacologic response to repaglinide should be monitored more closely whenever a protease inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484358, 'Fosaprepitant', 'Repaglinide', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of repaglinide, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, pharmacologic response to repaglinide should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. Patients should be advised to regularly monitor their blood sugar and counseled on how to recognize and treat hypoglycemia, which may include symptoms such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, and palpitations.', 'Metabolism', 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, pharmacologic response to repaglinide should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
