INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484549, 'Zanubrutinib', 'Reteplase', 'Major', 'Coadministration of zanubrutinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', 'DDInter', 1767317477, 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution. Close clinical and laboratory monitoring for bleeding complications is recommended during therapy. Patients should be advised to promptly report any signs and symptoms of bleeding to their physician. Discontinue zanubrutinib if intracranial hemorrhage of any grade occurs.', 'Synergism', 'Concomitant use of zanubrutinib in patients receiving other medications that interfere with platelet function or coagulation should be approached with caution.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484550, 'Amoxapine', 'Revefenacin', 'Moderate', 'The potential exists for additive anticholinergic effects such as mydriasis, blurred vision, heat intolerance, fever, dry mouth, tachycardia, urinary retention, constipation, and glaucoma (onset or exacerbation) when topical or inhaled anticholinergic agents are used with each other or with other agents that possess anticholinergic properties. The risk of systemic anticholinergic effects following topical administration depends on variables such as strength of the product, size of the application area, frequency of application, and use of occlusive dressing. Systemic effects are uncommon following oral inhalation or nasal administration due to the poor absorption of quaternary ammonium compounds from gastrointestinal and nasal mucosa.', 'DDInter', 1767317477, 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide). Caution is advised if concomitant use cannot be avoided, particularly in the elderly and those with significantly impaired renal and/or hepatic function.', 'Synergism', 'Topical and inhaled anticholinergic preparations should preferably not be used in combination with other anticholinergic agents or agents with significant anticholinergic effects such as antihistamines, antispasmodics, neuroleptics, phenothiazines, skeletal muscle relaxants, tricyclic antidepressants, and class IA antiarrhythmics (especially disopyramide).', NULL, NULL, 'Moderate', 'pharmacodynamic');
