INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484686, 'Etravirine', 'Ribociclib', 'Moderate', 'Coadministration of etravirine with a drug that is both a substrate as well as inhibitor of CYP450 3A4 may result in increased plasma concentrations of etravirine and decreased plasma concentrations of the other drug. Etravirine itself is a substrate and inducer of CYP450 3A4. Theoretically, etravirine may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', 'DDInter', 1767317477, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4. In addition, dosage adjustments may be required for the coadministered drug if it is also a substrate of CYP450 3A4.', 'Metabolism', 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with a drug that is an inhibitor of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484687, 'Everolimus', 'Ribociclib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 and P-glycoprotein may significantly increase the plasma concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus. The risk of side effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycaemia, and hyperlipidemia may be increased.', 'DDInter', 1767317477, 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors. The manufacturer''s drug product labeling should be consulted for specific recommendations.', 'Metabolism, Absorption', 'Blood trough levels should be closely monitored, and the dosage adjusted accordingly during concomitant administration and after discontinuation of moderate CYP450 3A4 and P-glycoprotein inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
