INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484689, 'Fedratinib', 'Ribociclib', 'Minor', 'Coadministration of fedratinib with moderate CYP450 3A4 inhibitors may increase the plasma concentrations and the risk of adverse effects of fedratinib, which is a substrate of the isoenzyme. However, clinical data are not available. Until further information is available, caution and monitoring for adverse effects including anemia, thrombocytopenia, gastrointestinal toxicity, and hepatic and pancreatic enzyme elevations is advised if fedratinib is used with moderate CYP450 3A4 inhibitors.', 'DDInter', 1767317477, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
