INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484777, 'Rifampicin', 'Ribociclib', 'Major', 'Coadministration with potent CYP450 3A4 inducers may significantly decrease the plasma concentrations and therapeutic effects of ribociclib, which is a substrate of the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of ribociclib with potent CYP450 3A4 inducers should generally be avoided. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If coadministration is necessary, dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inducer is added to or withdrawn from therapy. Patients should be monitored for therapeutic failure.', 'Metabolism', 'Concomitant use of ribociclib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
