INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1484882, 'Venlafaxine', 'Ribociclib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 such as azole antifungal agents and ketolide/macrolide antibiotics may increase the plasma concentrations of venlafaxine and its metabolite, O-desmethylvenlafaxine (ODV). The possibility of prolonged and/or increased pharmacologic effects of venlafaxine should be considered. Venlafaxine may cause dose-related prolongation of the QT interval. Theoretically, coadministration with other agents that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics may result in additive effects and increased risk of ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317477, 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Caution is recommended when venlafaxine is used with CYP450 3A4 inhibitors that can prolong the QT interval such as azole antifungal agents and ketolide/macrolide antibiotics.', NULL, NULL, 'Moderate', 'pharmacodynamic');
