    ) VALUES (1485096, 'Voriconazole', 'Rifampicin', 'Major', 'Coadministration with rifampin may significantly reduce the plasma concentrations of voriconazole. The mechanism is rifampin induction of CYP450 isoenzymes that are responsible for the metabolic clearance of voriconazole.', NULL, 'The use of voriconazole with rifampin is considered contraindicated.', NULL, 'The use of voriconazole with rifampin is considered contraindicated.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
